#!/usr/bin/env python3
# calc_daltons.py

"""
Calculates the estimated molecular weight of the protein in kilodaltons and writes the result on the screen
"""

P = ""
P = P.replace('\r', '').replace('\n', '')
AW = 0.11
PL = len(P)
print("The length of 'Protein kinase C beta type' is:" + " " + str(PL))
print("The average weight of this protein sequence in kilodaltons is:" + " " + str(PL*AW))
